   794	static void video_log_hex(const char *prefix, uint64_t value)
   795	{
   796	    static const char hex[] = "0123456789ABCDEF";
   797	    char buf[17];
   798	    buf[16] = '\0';
   799	    for (int i = 15; i >= 0; --i)
   800	    {
   801	        buf[i] = hex[value & 0xF];
   802	        value >>= 4;
   803	    }
   804	    serial_write_string(prefix);
   805	    serial_write_string(buf);
   806	    serial_write_string("\r\n");
   807	}
