static size_t cli_read_line(char *buffer, size_t capacity)
{
    //serial_write_string("shell.c: cli_read_line in\n");

    size_t len = 0;
    while (1)
    {
        char c = cli_get_char();

        if (c == '\r')
        {
            c = '\n';
        }

        if ((c == '\b' || c == 0x7F))
        {
            if (len > 0)
            {
                --len;
                console_backspace();
                serial_write_string("\b \b");
            }
            continue;
        }

        if (c == '\n')
        {
            console_putc('\n');
            serial_emit_char('\n');
            buffer[len] = '\0';
            return len;
        }

        if (c >= ' ' && len < capacity - 1)
        {
            buffer[len++] = c;
            console_putc(c);
            serial_write_char(c);
        }
    }

    //serial_write_string("shell.c: cli_read_line out\n");
}

static bool is_space(char c)
{
    return c == ' ' || c == '\t';
}

static char *trim_whitespace(char *text)
{
    if (!text)
    {
        return text;
    }
    while (*text && is_space(*text))
    {
        ++text;
    }
    size_t len = strlen(text);
    while (len > 0 && is_space(text[len - 1]))
    {
        text[--len] = '\0';
    }
    return text;
}

static void serial_emit_char(char c)
{
    if (c == '\n')
    {
        serial_write_char('\r');
    }
    serial_write_char(c);
}

static void shell_stream_console_write(void *context, const char *data, size_t len)
{
    (void)context;
    for (size_t i = 0; i < len; ++i)
    {
        char c = data[i];
        console_putc(c);
        serial_emit_char(c);
    }
}
