        serial_write_char(hex[pic2_mask & 0xF]);
        serial_write_string(" irq=");
        serial_write_char(hex[(irq >> 4) & 0xF]);
        serial_write_char(hex[irq & 0xF]);
        serial_write_string("\r\n");
