    while (start[total_len] && start[total_len] != ' ' && start[total_len] != '\t')
    {
        if (total_len + 1 < capacity)
        {
            out[total_len] = start[total_len];
        }
        ++total_len;
    }
    size_t copy_len = total_len;
    if (copy_len >= capacity)
    {
        copy_len = capacity - 1;
    }
    out[copy_len] = '\0';
    *cursor = start + total_len;
    return true;
}

