static ssize_t console_stdout_write(void *ctx, const void *buffer, size_t count)
{
    (void)ctx;
    if (!buffer)
    {
        return 0;
    }
    const char *data = (const char *)buffer;
    for (size_t i = 0; i < count; ++i)
    {
        char c = data[i];
        console_putc(c);
        if (c == '\n')
        {
            serial_write_char('\r');
        }
        serial_write_char(c);
    }
    return (ssize_t)count;
}

