{
    if (!scrollbar || !scrollbar->used)
    {
        return false;
    }
    int x0 = origin_x + scrollbar->x;
    int y0 = origin_y + scrollbar->y;
    int x1 = x0 + scrollbar->width;
    int y1 = y0 + scrollbar->height;
    return (px >= x0 && px < x1 && py >= y0 && py < y1);
