+#include "arch/x86/bootlayout.h"
+#include "bootinfo.h"
+#include "uefi.h"
+
+#include <stdbool.h>
+#include <stddef.h>
+#include <stdint.h>
+
+extern void kernel_entry(void);
+
+extern uint8_t __kernel_vma_start[];
+extern uint8_t __kernel_vma_end[];
+
+bootinfo_t boot_info __attribute__((aligned(64))) = {
+    .magic = BOOTINFO_MAGIC,
+    .version = BOOTINFO_VERSION,
+};
+
+static EFI_SYSTEM_TABLE *g_system_table __attribute__((section(".uefi.data"))) = NULL;
+static EFI_BOOT_SERVICES *g_boot_services __attribute__((section(".uefi.data"))) = NULL;
+
+static void EFIAPI_SECTION noop(void) { }
+
+static void __attribute__((section(".uefi.text"))) halt_forever(void)
+{
+    for (;;)
+    {
+        __asm__ volatile ("hlt");
+    }
+}
+
+static bool __attribute__((section(".uefi.text"))) guid_equal(const EFI_GUID *a, const EFI_GUID *b)
+{
+    if (!a || !b)
+    {
+        return false;
+    }
+    if (a->Data1 != b->Data1 || a->Data2 != b->Data2 || a->Data3 != b->Data3)
+    {
+        return false;
+    }
+    for (size_t i = 0; i < sizeof(a->Data4); ++i)
+    {
+        if (a->Data4[i] != b->Data4[i])
+        {
+            return false;
+        }
+    }
+    return true;
+}
+
+static void __attribute__((section(".uefi.text"))) early_console_write(const CHAR16 *text)
+{
+    if (!g_system_table || !g_system_table->ConOut || !g_system_table->ConOut->OutputString || !text)
+    {
+        return;
+    }
+    g_system_table->ConOut->OutputString(g_system_table->ConOut, text);
+}
+
+static void __attribute__((section(".uefi.text"))) panic_efi(const CHAR16 *reason)
+{
+    early_console_write(L"[alix] UEFI panic: ");
+    early_console_write(reason);
+    early_console_write(L"\r\n");
+    halt_forever();
+}
+
+static uint32_t __attribute__((section(".uefi.text"))) efi_type_to_e820(uint32_t type)
+{
+    switch (type)
+    {
+        case EfiConventionalMemory:
+        case EfiLoaderCode:
+        case EfiLoaderData:
+        case EfiBootServicesCode:
+        case EfiBootServicesData:
+            return 1;
+        case EfiACPIReclaimMemory:
+            return 3;
+        case EfiACPIMemoryNVS:
+            return 4;
+        default:
+            return 2;
+    }
+}
+
+static void __attribute__((section(".uefi.text"))) populate_acpi_rsdp(void)
+{
+    if (!g_system_table)
+    {
+        return;
+    }
+
+    EFI_GUID acpi20 = EFI_ACPI_20_TABLE_GUID;
+    EFI_GUID acpi10 = EFI_ACPI_TABLE_GUID;
+
+    for (UINTN i = 0; i < g_system_table->NumberOfTableEntries; ++i)
+    {
+        EFI_CONFIGURATION_TABLE *entry = &g_system_table->ConfigurationTable[i];
+        if (!entry->VendorTable)
+        {
+            continue;
+        }
+        if (guid_equal(&entry->VendorGuid, &acpi20) || guid_equal(&entry->VendorGuid, &acpi10))
+        {
+            boot_info.acpi_rsdp = (uint64_t)(uintptr_t)entry->VendorTable;
+            return;
+        }
+    }
+}
+
+static void __attribute__((section(".uefi.text"))) populate_framebuffer_info(void)
+{
+    if (!g_boot_services)
+    {
+        return;
+    }
+
+    EFI_GUID gop_guid = EFI_GRAPHICS_OUTPUT_PROTOCOL_GUID;
+    EFI_GRAPHICS_OUTPUT_PROTOCOL *gop = NULL;
+    EFI_STATUS status = g_boot_services->LocateProtocol(&gop_guid, NULL, (void **)&gop);
+    if (status != EFI_SUCCESS || !gop || !gop->Mode || !gop->Mode->Info)
+    {
+        boot_info.framebuffer_enabled = false;
+        return;
+    }
+
+    boot_info.framebuffer_enabled = true;
+    boot_info.framebuffer_base = (uint64_t)gop->Mode->FrameBufferBase;
+    boot_info.framebuffer_size = (uint64_t)gop->Mode->FrameBufferSize;
+    boot_info.framebuffer_width = gop->Mode->Info->HorizontalResolution;
+    boot_info.framebuffer_height = gop->Mode->Info->VerticalResolution;
+    boot_info.framebuffer_pitch = gop->Mode->Info->PixelsPerScanLine;
+    boot_info.framebuffer_bpp = 32;
+}
+
+static void __attribute__((section(".uefi.text"))) copy_memory(void *dst, const void *src, size_t len)
+{
+    uint8_t *d = (uint8_t *)dst;
+    const uint8_t *s = (const uint8_t *)src;
+    while (len--)
+    {
+        *d++ = *s++;
+    }
+}
+
+static void __attribute__((section(".uefi.text"))) reserve_region(uint64_t base, uint64_t size)
+{
+    if (!g_boot_services || size == 0)
+    {
+        return;
+    }
+    uint64_t aligned_size = (size + 0xFFFULL) & ~0xFFFULL;
+    UINTN pages = (UINTN)(aligned_size >> 12);
+    EFI_PHYSICAL_ADDRESS address = (EFI_PHYSICAL_ADDRESS)base;
+    EFI_STATUS status = g_boot_services->AllocatePages(EfiAllocateAddress, EfiLoaderData, pages, &address);
+    if (status != EFI_SUCCESS)
+    {
+        panic_efi(L"AllocatePages failed");
+    }
+}
+
+static void __attribute__((section(".uefi.text"))) prepare_identity_regions(void)
+{
+    uint64_t kernel_start = (uint64_t)(uintptr_t)__kernel_vma_start;
+    uint64_t kernel_end = (uint64_t)(uintptr_t)__kernel_vma_end;
+    reserve_region(kernel_start, kernel_end - kernel_start);
+    reserve_region(PAGE_TABLE_BASE, PAGE_TABLE_PAGES * 0x1000ULL);
+    reserve_region(STACK_TOP - STACK_SIZE, STACK_SIZE);
+}
+
+static void __attribute__((section(".uefi.text"))) convert_memory_map(EFI_MEMORY_DESCRIPTOR *map,
+                                                                      UINTN map_size,
+                                                                      UINTN desc_size)
+{
+    boot_info.e820_entry_count = 0;
+    if (!map || map_size == 0 || desc_size == 0)
+    {
+        return;
+    }
+
+    UINTN descriptor_count = map_size / desc_size;
+    uint8_t *cursor = (uint8_t *)map;
+    for (UINTN i = 0; i < descriptor_count && boot_info.e820_entry_count < BOOTINFO_MAX_E820_ENTRIES; ++i)
+    {
+        EFI_MEMORY_DESCRIPTOR *desc = (EFI_MEMORY_DESCRIPTOR *)(cursor + (i * desc_size));
+        bootinfo_e820_entry_t *entry = &boot_info.e820[boot_info.e820_entry_count++];
+        entry->base = desc->PhysicalStart;
+        entry->length = desc->NumberOfPages * 4096ULL;
+        entry->type = efi_type_to_e820(desc->Type);
+        entry->attr = (uint32_t)(desc->Attribute & 0xFFFFFFFFULL);
+    }
+}
+
+static EFI_STATUS __attribute__((section(".uefi.text"))) exit_boot_services_with_map(EFI_HANDLE image_handle)
+{
+    EFI_MEMORY_DESCRIPTOR *map_buffer = NULL;
+    UINTN map_size = 0;
+    UINTN desc_size = 0;
+    UINTN map_key = 0;
+    uint32_t desc_version = 0;
+
+    for (;;)
+    {
+        EFI_STATUS status = g_boot_services->GetMemoryMap(&map_size, map_buffer, &map_key, &desc_size, &desc_version);
+        if (status != EFI_BUFFER_TOO_SMALL)
+        {
+            return status;
+        }
+
+        map_size += desc_size * 2;
+        status = g_boot_services->AllocatePool(EfiLoaderData, map_size, (void **)&map_buffer);
+        if (status != EFI_SUCCESS)
+        {
+            return status;
+        }
+
+        status = g_boot_services->GetMemoryMap(&map_size, map_buffer, &map_key, &desc_size, &desc_version);
+        if (status != EFI_SUCCESS)
+        {
+            continue;
+        }
+
+        convert_memory_map(map_buffer, map_size, desc_size);
+
+        status = g_boot_services->ExitBootServices(image_handle, map_key);
+        if (status == EFI_SUCCESS)
+        {
+            return EFI_SUCCESS;
+        }
+
+        if (status != EFI_INVALID_PARAMETER)
+        {
+            return status;
+        }
+    }
+}
+
+EFI_STATUS __attribute__((section(".uefi.text"), used, EFIAPI)) efi_main(EFI_HANDLE image_handle,
+                                                                        EFI_SYSTEM_TABLE *system_table)
+{
+    g_system_table = system_table;
+    g_boot_services = system_table ? system_table->BootServices : NULL;
+    if (!g_boot_services)
+    {
+        panic_efi(L"Boot services unavailable");
+    }
+
+    boot_info.magic = BOOTINFO_MAGIC;
+    boot_info.version = BOOTINFO_VERSION;
+    boot_info.e820_entry_count = 0;
+    boot_info.framebuffer_enabled = false;
+    boot_info.framebuffer_base = 0;
+    boot_info.framebuffer_size = 0;
+    boot_info.acpi_rsdp = 0;
+
+    populate_framebuffer_info();
+    populate_acpi_rsdp();
+    prepare_identity_regions();
+
+    EFI_STATUS status = exit_boot_services_with_map(image_handle);
+    if (status != EFI_SUCCESS)
+    {
+        panic_efi(L"ExitBootServices failed");
+    }
+
+    kernel_entry();
+    halt_forever();
+    return EFI_SUCCESS;
+}
