static void console_write_hex(uint64_t value)
{
    CHAR16 digits[17];
    for (int i = 15; i >= 0; --i)
    {
        uint8_t nibble = (uint8_t)(value & 0xF);
        digits[i] = (CHAR16)((nibble < 10) ? (L'0' + nibble) : (L'A' + (nibble - 10)));
        value >>= 4;
    }
    digits[16] = L'\0';
    console_write(digits);
}

