static void convert_to_e820(bootinfo_t *info,
                            EFI_MEMORY_DESCRIPTOR *map,
                            UINTN map_size,
                            UINTN descriptor_size)
{
    info->e820_entry_count = 0;
    UINTN entry_count = map_size / descriptor_size;
    uint8_t *cursor = (uint8_t *)map;
    for (UINTN i = 0; i < entry_count; ++i)
    {
        if (info->e820_entry_count >= BOOTINFO_MAX_E820_ENTRIES)
        {
            break;
        }
        EFI_MEMORY_DESCRIPTOR *desc = (EFI_MEMORY_DESCRIPTOR *)(cursor + (i * descriptor_size));
        bootinfo_e820_entry_t *entry = &info->e820[info->e820_entry_count++];
        entry->base = desc->PhysicalStart;
        entry->length = desc->NumberOfPages * EFI_PAGE_SIZE;
        entry->type = efi_type_to_e820(desc->Type);
        entry->attr = (uint32_t)(desc->Attribute & 0xFFFFFFFFULL);
    }
}

static EFI_STATUS exit_boot_services(EFI_HANDLE image_handle, bootinfo_t *info)
{
    if (!g_bs) return EFI_LOAD_ERROR;

    // 1) Quiesce firmware sources of churn
    if (g_bs->SetWatchdogTimer) {
        // Disable UEFI watchdog so it can't fire during the critical window
        g_bs->SetWatchdogTimer(0, 0, 0, NULL);
    }

    EFI_TPL old_tpl = 0;
    if (g_bs->RaiseTPL) {
        old_tpl = g_bs->RaiseTPL(TPL_HIGH_LEVEL);
    }

    EFI_MEMORY_DESCRIPTOR *map = NULL;
    UINTN capacity = 0;

    EFI_STATUS status;
    UINTN map_size = 0;
    UINTN map_key = 0;
    UINTN descriptor_size = 0;
    uint32_t descriptor_version = 0;

    // Try a bounded number of times; with TPL raised you should succeed on first or second try.
    for (UINTN attempt = 1; attempt <= 16; ++attempt)
    {
        // First call to learn required size and descriptor size
        map_size = 0;
        status = g_bs->GetMemoryMap(&map_size, map, &map_key, &descriptor_size, &descriptor_version);

        if (status == EFI_BUFFER_TOO_SMALL) {
            // Add headroom (two extra descriptors) and (re)allocate if needed
            UINTN needed = map_size + (descriptor_size * 2);
            if (needed > capacity) {
                if (map) g_bs->FreePool(map);
                capacity = needed;
                status = g_bs->AllocatePool(EfiLoaderData, capacity, (void **)&map);
                if (efi_error(status) || !map) {
                    // Restore TPL before returning
                    if (g_bs->RestoreTPL) g_bs->RestoreTPL(old_tpl);
                    return status;
                }
            }

            // Second call: actually retrieve the map into our buffer
            status = g_bs->GetMemoryMap(&map_size, map, &map_key, &descriptor_size, &descriptor_version);
            if (efi_error(status)) {
                if (status != EFI_BUFFER_TOO_SMALL) {
                    if (g_bs->RestoreTPL) g_bs->RestoreTPL(old_tpl);
                    return status;
                }
                // If it grew again, loop to resize
                continue;
            }
        } else if (efi_error(status)) {
            if (g_bs->RestoreTPL) g_bs->RestoreTPL(old_tpl);
            return status;
        }

        // Fill your bootinfo snapshot *without* calling any Boot Services.
        convert_to_e820(info, map, map_size, descriptor_size);

        // Critical handoff: no logging or BS calls before this point
        status = g_bs->ExitBootServices(image_handle, map_key);
        if (status == EFI_SUCCESS) {
            // We intentionally do not FreePool(map); Boot Services no longer exist.
            return EFI_SUCCESS;
        }

        // If we fail for anything other than "key changed", bubble up.
        if (status != EFI_INVALID_PARAMETER) {
            if (g_bs->RestoreTPL) g_bs->RestoreTPL(old_tpl);
            return status;
        }

        // Key changed: loop and try again. We reuse the same buffer.
    }

    if (g_bs->RestoreTPL) g_bs->RestoreTPL(old_tpl);
    return EFI_INVALID_PARAMETER;
}

