        return NULL;
    }
    uintptr_t addr = (uintptr_t)(raw + sizeof(uintptr_t));
    uintptr_t aligned = (addr + (align - 1)) & ~(uintptr_t)(align - 1);
    ((uintptr_t *)aligned)[-1] = (uintptr_t)raw;
    memset((void *)aligned, 0, size);
