    idt_set_gate(14, (void *)page_fault_handler);
    idt_set_gate(32, (void *)irq0_handler);
    idt_set_gate(33, (void *)irq1_handler);
    idt_set_gate(43, (void *)irq11_handler);
    idt_set_gate(44, (void *)irq12_handler);
