+        int target_x = origin_x + bmp->offset_x;
+        int target_y = origin_y - bmp->offset_y;
+
+        for (int y = 0; y < bmp->height; ++y)
+        {
+            int dst_y = target_y + y;
+            if (dst_y < 0 || dst_y >= atlas_height)
+            {
