static void rtl8139_tx_check_stuck(const char *context)
{
    if (!g_rtl_present)
    {
        return;
    }
    uint64_t now = timer_ticks();
    for (int slot = 0; slot < RTL_TX_SLOT_COUNT; ++slot)
    {
        rtl8139_tx_meta_t *meta = &g_tx_meta[slot];
        if (!meta->valid)
