            rtl8139_log_hex8((uint8_t)slot);
            serial_write_string(" dticks=0x");
            rtl8139_log_hex32((uint32_t)delta);
            serial_write_string(" tsd=0x");
            rtl8139_log_hex32(tsd);
            serial_write_string(" ");
            rtl8139_log_tsd_status(tsd);
