    uint32_t dst_ip = socket ? socket->remote_ip : 0;

    sum += (src_ip >> 16) & 0xFFFFU;
    sum += src_ip & 0xFFFFU;
    sum += (dst_ip >> 16) & 0xFFFFU;
    sum += dst_ip & 0xFFFFU;
    sum += 6U;
    sum += tcp_len;

