static const char *find_substring(const char *haystack, const char *needle)
{
    size_t needle_len = strlen(needle);
    if (needle_len == 0)
    {
        return haystack;
    }
    const char *cursor = haystack;
    while (*cursor)
    {
        if (*cursor == needle[0])
        {
            if (strncmp(cursor, needle, needle_len) == 0)
            {
                return cursor;
            }
        }
        ++cursor;
    }
    return NULL;
}
