static void tcp_log_hex32(uint32_t value)
{
    static const char hex[] = "0123456789ABCDEF";
    char buf[8];
    for (int i = 0; i < 8; ++i)
    {
        buf[7 - i] = hex[value & 0xF];
        value >>= 4;
    }
    for (int i = 0; i < 8; ++i)
    {
        serial_write_char(buf[i]);
    }
}

