static uint8_t *read_entire_file(const char *path, size_t *size_out)
{
    if (!path)
    {
        return NULL;
    }

    int fd = open(path, 0);
    if (fd < 0)
    {
        return NULL;
    }

    size_t capacity = 4096;
    size_t size = 0;
    uint8_t *buffer = (uint8_t *)malloc(capacity);
    if (!buffer)
    {
        close(fd);
        return NULL;
    }

    for (;;)
    {
        if (size == capacity)
        {
            size_t new_capacity = capacity * 2;
            uint8_t *new_buffer = (uint8_t *)realloc(buffer, new_capacity);
            if (!new_buffer)
            {
                free(buffer);
                close(fd);
                return NULL;
            }
            buffer = new_buffer;
            capacity = new_capacity;
        }

        ssize_t bytes = read(fd, buffer + size, capacity - size);
        if (bytes < 0)
        {
            free(buffer);
            close(fd);
            return NULL;
        }
        if (bytes == 0)
        {
            break;
        }
        size += (size_t)bytes;
    }
    close(fd);
    if (size_out)
    {
        *size_out = size;
    }
    return buffer;
}

