static vfs_node_t *resolve_node(vfs_node_t *cwd, const char *path)
{
    if (!path || !*path) return cwd;

    vfs_node_t *node = (path[0] == '/') ? root : cwd;
    const char *cursor = path;

    cursor = skip_separators(cursor);
    if (*cursor == '\0') return node;

    char *component = NULL;
