{
    ahci_port_ctx_t *ctx = (ahci_port_ctx_t *)device->driver_data;
    uint8_t *dst = (uint8_t *)buffer;
    if (!count)
    {
        return true;
    }
    while (count > 0)
    {
        uint32_t chunk = (count > AHCI_MAX_TRANSFER_SECTORS) ? AHCI_MAX_TRANSFER_SECTORS : count;
