+    {
+        return false;
+    }
+    wolf_set_angle(g_player.angle + delta);
+    return true;
+}
+
+static uint16_t wolf_color_shade(uint8_t r, uint8_t g, uint8_t b, float shade)
+{
+    if (shade < 0.0f)
+    {
+        shade = 0.0f;
+    }
+    if (shade > 1.0f)
+    {
+        shade = 1.0f;
+    }
+    uint8_t sr = (uint8_t)(r * shade);
+    uint8_t sg = (uint8_t)(g * shade);
+    uint8_t sb = (uint8_t)(b * shade);
+    return video_make_color(sr, sg, sb);
+}
+
+static uint16_t wolf_wall_color(int tile, int side)
+{
+    static const struct
+    {
+        uint8_t r, g, b;
+    } palette[] = {
+        { 200, 200, 200 },
+        { 220, 120, 80 },
+        { 80, 160, 220 },
+        { 160, 100, 190 },
+        { 90, 180, 90 },
+        { 220, 210, 120 },
+    };
+
+    if (tile <= 0)
+    {
+        tile = 1;
+    }
+    size_t index = (size_t)(tile - 1) % (sizeof(palette) / sizeof(palette[0]));
+    float shade = (side == 0) ? 0.9f : 0.6f;
+    return wolf_color_shade(palette[index].r, palette[index].g, palette[index].b, shade);
+}
+
+static void wolf_fill_background(uint16_t *buffer, int width, int height)
+{
+    int half = height / 2;
+    for (int y = 0; y < height; ++y)
+    {
+        float t;
+        uint8_t r;
+        uint8_t g;
+        uint8_t b;
+
+        if (y < half)
+        {
+            t = (float)y / (float)(half ? half : 1);
+            r = (uint8_t)(20 + (uint8_t)(40.0f * (1.0f - t)));
+            g = (uint8_t)(60 + (uint8_t)(60.0f * (1.0f - t)));
+            b = (uint8_t)(120 + (uint8_t)(100.0f * (1.0f - t)));
+        }
+        else
+        {
+            int rel = y - half;
+            int denom = height - half;
+            if (denom <= 0)
+            {
+                denom = 1;
+            }
+            t = (float)rel / (float)denom;
+            r = (uint8_t)(40 + (uint8_t)(100.0f * t));
+            g = (uint8_t)(30 + (uint8_t)(80.0f * t));
+            b = (uint8_t)(20 + (uint8_t)(60.0f * t));
+        }
+
+        uint16_t color = video_make_color(r, g, b);
+        uint16_t *row = buffer + (size_t)y * (size_t)width;
+        for (int x = 0; x < width; ++x)
+        {
+            row[x] = color;
+        }
+    }
+}
+
+static void wolf_draw_column(uint16_t *buffer, int width, int height, int x)
+{
+    float camera_x = 2.0f * (float)x / (float)width - 1.0f;
+    float ray_dir_x = g_player.dir_x + g_player.plane_x * camera_x;
+    float ray_dir_y = g_player.dir_y + g_player.plane_y * camera_x;
+
+    int map_x = (int)g_player.x;
+    int map_y = (int)g_player.y;
+
+    float delta_dist_x = (ray_dir_x == 0.0f) ? 1e30f : wolf_absf(1.0f / ray_dir_x);
+    float delta_dist_y = (ray_dir_y == 0.0f) ? 1e30f : wolf_absf(1.0f / ray_dir_y);
+
+    float side_dist_x;
+    float side_dist_y;
+    int step_x;
+    int step_y;
+
+    if (ray_dir_x < 0.0f)
+    {
+        step_x = -1;
+        side_dist_x = (g_player.x - map_x) * delta_dist_x;
+    }
+    else
+    {
+        step_x = 1;
+        side_dist_x = (map_x + 1.0f - g_player.x) * delta_dist_x;
+    }
+
+    if (ray_dir_y < 0.0f)
+    {
+        step_y = -1;
+        side_dist_y = (g_player.y - map_y) * delta_dist_y;
+    }
+    else
+    {
+        step_y = 1;
+        side_dist_y = (map_y + 1.0f - g_player.y) * delta_dist_y;
+    }
+
+    int tile = 0;
+    int side = 0;
+    for (int i = 0; i < MAX_RAY_STEPS; ++i)
+    {
+        if (side_dist_x < side_dist_y)
+        {
+            side_dist_x += delta_dist_x;
+            map_x += step_x;
+            side = 0;
+        }
+        else
+        {
+            side_dist_y += delta_dist_y;
+            map_y += step_y;
+            side = 1;
+        }
+
+        if (map_x < 0 || map_y < 0 || map_x >= MAP_WIDTH || map_y >= MAP_HEIGHT)
+        {
+            tile = 1;
+            break;
+        }
+
+        tile = g_world_map[map_y][map_x];
+        if (tile != 0)
+        {
+            break;
+        }
+    }
