+    if (tile == 0)
+    {
+        return;
+    }
+
+    float perp_wall_dist = (side == 0) ? (side_dist_x - delta_dist_x) : (side_dist_y - delta_dist_y);
+    if (perp_wall_dist <= 0.0001f)
+    {
+        perp_wall_dist = 0.0001f;
+    }
+
+    int line_height = (int)((float)height / perp_wall_dist);
+    int draw_start = (height / 2) - (line_height / 2);
+    int draw_end = (height / 2) + (line_height / 2);
+
+    if (draw_start < 0)
+    {
+        draw_start = 0;
+    }
+    if (draw_end >= height)
+    {
+        draw_end = height - 1;
+    }
