    if (irq12_log_count < 16)
    {
        serial_write_string("irq12 data=0x");
        static const char hex[] = "0123456789ABCDEF";
        serial_write_char(hex[(data >> 4) & 0xF]);
        serial_write_char(hex[data & 0xF]);
        serial_write_string("\r\n");
    }
    mouse_on_irq(data);
    pic_send_eoi(12);
    irq12_log_count++;
}

