#define KEYBOARD_REPEAT_MULTI_PATH "keyboard/repeat/multi_mode"

static uint32_t repeat_initial_delay_ms = 500;
static uint32_t repeat_interval_ms = 200;
static uint32_t repeat_initial_delay_ticks = 50;
