static void shell_command_runner(void *arg)
{
    shell_command_task_t *task = (shell_command_task_t *)arg;
    bool ok = false;
    if (task && task->handler)
    {
        ok = task->handler(task->shell, task->output, task->args);
        task->result = ok;
    }
    process_exit(ok ? 0 : 1);
}

