    char *name = NULL;

    if (!split_parent_and_name(cwd ? cwd : root, path, &parent, &name))
        return NULL;

    vfs_node_t *existing = vfs_find_child(parent, name);
    if (existing)
    {
        free(name);
        return (existing->type == VFS_NODE_DIR) ? existing : NULL;
    }

