+static uint16_t rgb565_blend(uint16_t bg, uint16_t fg, uint8_t alpha)
+{
+    if (alpha == 0)
+    {
+        return bg;
+    }
+    if (alpha == 255)
+    {
+        return fg;
+    }
+
+    uint16_t br = (uint16_t)((((bg >> 11) & 0x1F) * 527 + 23) >> 6);
+    uint16_t bgc = (uint16_t)((((bg >> 5) & 0x3F) * 259 + 33) >> 6);
+    uint16_t bb = (uint16_t)(((bg & 0x1F) * 527 + 23) >> 6);
+
+    uint16_t fr = (uint16_t)((((fg >> 11) & 0x1F) * 527 + 23) >> 6);
+    uint16_t fgc = (uint16_t)((((fg >> 5) & 0x3F) * 259 + 33) >> 6);
+    uint16_t fb = (uint16_t)(((fg & 0x1F) * 527 + 23) >> 6);
+
+    uint16_t rr = (uint16_t)((fr * alpha + br * (255 - alpha)) / 255);
+    uint16_t rg = (uint16_t)((fgc * alpha + bgc * (255 - alpha)) / 255);
+    uint16_t rb = (uint16_t)((fb * alpha + bb * (255 - alpha)) / 255);
+
+    return (uint16_t)(((rr & 0xF8) << 8) | ((rg & 0xFC) << 3) | (rb >> 3));
+}
+
+static bool menu_font_read_user(uint8_t **data_out, size_t *size_out)
+{
+#ifdef ATK_NO_DESKTOP_APPS
+    if (!data_out || !size_out)
+    {
+        return false;
+    }
+
+    int fd = open(MENU_FONT_PATH, SYSCALL_OPEN_READ);
+    if (fd < 0)
+    {
+        return false;
+    }
+
+    size_t capacity = 0;
+    size_t size = 0;
+    uint8_t *buffer = NULL;
+
+    while (1)
+    {
+        if (size == capacity)
+        {
+            size_t new_capacity = capacity ? capacity * 2 : 4096;
+            uint8_t *new_buffer = (uint8_t *)realloc(buffer, new_capacity);
+            if (!new_buffer)
+            {
+                free(buffer);
+                close(fd);
+                return false;
+            }
+            buffer = new_buffer;
+            capacity = new_capacity;
+        }
+
+        ssize_t bytes = read(fd, buffer + size, capacity - size);
+        if (bytes < 0)
+        {
+            free(buffer);
+            close(fd);
+            return false;
+        }
+        if (bytes == 0)
+        {
+            break;
+        }
+        size += (size_t)bytes;
+    }
+
+    close(fd);
+
+    if (size == 0)
+    {
+        free(buffer);
+        return false;
+    }
+
+    *data_out = buffer;
+    *size_out = size;
+    return true;
+#else
+    (void)data_out;
+    (void)size_out;
+    return false;
+#endif
+}
+
+static bool menu_font_load(void)
+{
+    if (g_menu_font.tried)
+    {
+        return g_menu_font.ready;
+    }
+    g_menu_font.tried = true;
+
+#ifndef ATK_NO_DESKTOP_APPS
+    vfs_node_t *node = vfs_open_file(vfs_root(), MENU_FONT_PATH, false, false);
+    if (!node)
+    {
+        return false;
+    }
+    size_t size = 0;
+    const char *data = vfs_data(node, &size);
+    if (!data || size == 0)
+    {
+        return false;
+    }
+    if (!ttf_font_load(&g_menu_font.font, (const uint8_t *)data, size))
+    {
+        return false;
+    }
+#else
+    uint8_t *buffer = NULL;
+    size_t size = 0;
+    if (!menu_font_read_user(&buffer, &size))
+    {
+        return false;
+    }
+    bool ok = ttf_font_load(&g_menu_font.font, buffer, size);
+    free(buffer);
+    if (!ok)
+    {
+        return false;
+    }
+#endif
+
+    if (!ttf_font_metrics(&g_menu_font.font, MENU_FONT_PIXEL_SIZE, &g_menu_font.metrics))
+    {
+        ttf_font_unload(&g_menu_font.font);
+        g_menu_font.font.impl = NULL;
+        return false;
+    }
+
+    g_menu_font.pixel_size = MENU_FONT_PIXEL_SIZE;
+    g_menu_font.ready = true;
+    return true;
+}
+
+static bool menu_font_prepare_glyphs(const char *text,
+                                     menu_font_glyph_t *glyphs,
+                                     size_t *glyph_count,
+                                     int *text_width)
+{
+    if (!text || !glyphs || !glyph_count)
+    {
+        return false;
+    }
+
+    size_t count = 0;
+    int width = 0;
+
+    for (const char *cursor = text; *cursor && count < MENU_FONT_MAX_GLYPHS; ++cursor)
+    {
+        uint8_t ch = (uint8_t)*cursor;
+        ttf_bitmap_t bitmap;
+        ttf_glyph_metrics_t metrics;
+        memset(&bitmap, 0, sizeof(bitmap));
+        memset(&metrics, 0, sizeof(metrics));
+
+        if (!ttf_font_render_glyph_bitmap(&g_menu_font.font,
+                                          (uint32_t)ch,
+                                          g_menu_font.pixel_size,
+                                          &bitmap,
+                                          &metrics))
+        {
+            menu_font_destroy_glyphs(glyphs, count);
+            return false;
+        }
+
+        glyphs[count].codepoint = ch;
+        glyphs[count].bitmap = bitmap;
+        glyphs[count].metrics = metrics;
+        width += metrics.advance;
+        ++count;
+    }
+
+    if (glyph_count)
+    {
+        *glyph_count = count;
+    }
+    if (text_width)
+    {
+        *text_width = width;
+    }
+    return true;
+}
+
+static void menu_font_destroy_glyphs(menu_font_glyph_t *glyphs, size_t count)
+{
+    if (!glyphs)
+    {
+        return;
+    }
+    for (size_t i = 0; i < count; ++i)
+    {
+        ttf_bitmap_destroy(&glyphs[i].bitmap);
+    }
+}
+
+static void menu_font_draw_glyphs(int start_x,
+                                  int baseline_y,
+                                  const menu_font_glyph_t *glyphs,
+                                  size_t glyph_count,
+                                  uint16_t fg,
+                                  uint16_t bg)
+{
+    int pen_x = start_x;
+
+    for (size_t i = 0; i < glyph_count; ++i)
+    {
+        const ttf_bitmap_t *bmp = &glyphs[i].bitmap;
+        const ttf_glyph_metrics_t *metrics = &glyphs[i].metrics;
+
+        if (bmp->pixels && bmp->width > 0 && bmp->height > 0)
+        {
+            size_t pixel_count = (size_t)bmp->width * (size_t)bmp->height;
+            uint16_t *buffer = (uint16_t *)malloc(pixel_count * sizeof(uint16_t));
+            if (buffer)
+            {
+                for (int y = 0; y < bmp->height; ++y)
+                {
+                    for (int x = 0; x < bmp->width; ++x)
+                    {
+                        uint8_t alpha = bmp->pixels[y * bmp->stride + x];
+                        buffer[(size_t)y * (size_t)bmp->width + (size_t)x] = rgb565_blend(bg, fg, alpha);
+                    }
+                }
+
+                int dst_x = pen_x + bmp->offset_x;
+                int dst_y = baseline_y - bmp->offset_y;
+                video_blit_rgb565(dst_x,
+                                  dst_y,
+                                  bmp->width,
+                                  bmp->height,
+                                  buffer,
+                                  bmp->width * (int)sizeof(uint16_t));
+                free(buffer);
+            }
+        }
+
+        pen_x += metrics->advance;
+    }
+}
