        return -1;
    }

    const int margin = 8;
    int content_offset_x = margin;
    int content_offset_y = ATK_WINDOW_TITLE_HEIGHT + margin;
    window->width = desc.width + margin * 2;
    window->height = desc.height + margin * 2 + ATK_WINDOW_TITLE_HEIGHT;
