static bool wolf_handle_key(char ch, bool *running)
{
    bool updated = false;
    if (ch >= 'A' && ch <= 'Z')
    {
        ch = (char)(ch - 'A' + 'a');
    }

    switch (ch)
    {
        case 'w':
            updated = wolf_move_forward();
            break;
        case 's':
            updated = wolf_move_backward();
            break;
        case 'a':
            updated = wolf_rotate(-ROTATE_SPEED);
            break;
        case 'd':
            updated = wolf_rotate(ROTATE_SPEED);
            break;
        case 'q':
            updated = wolf_strafe_left();
            break;
        case 'e':
            updated = wolf_strafe_right();
            break;
        case 27:
            *running = false;
            break;
        default:
            break;
    }
    return updated;
}

