static void enqueue_thread(thread_t *thread)
{
    if (!thread || thread->in_run_queue)
    {
        return;
    }
    thread->queue_next = NULL;
    thread->in_run_queue = true;
    if (!g_run_queue_head)
    {
        g_run_queue_head = thread;
        g_run_queue_tail = thread;
    }
    else
    {
        g_run_queue_tail->queue_next = thread;
        g_run_queue_tail = thread;
