    uint64_t wake_tick = timer_ticks() + ticks;
    uint64_t flags = cpu_save_flags();
    cpu_cli();
    thread->state = THREAD_STATE_BLOCKED;
    thread->sleep_until_tick = wake_tick;
    sleep_queue_insert(thread);
