    if (!win)
    {
        return;
    }

    win->window = NULL;
    win->image = NULL;
    win->pixels = NULL;
    win->closed = true;
    if (g_focus_window == win)
    {
        g_focus_window = NULL;
    }
    if (g_capture_window == win)
    {
        g_capture_window = NULL;
    }
    user_atk_send_close_event(win);
}

