+
+static thread_t *wait_queue_dequeue_locked(wait_queue_t *queue)
+{
+    if (!queue)
+    {
+        return NULL;
+    }
+    thread_t *thread = queue->head;
+    if (!thread)
+    {
+        return NULL;
+    }
+    queue->head = thread->wait_queue_next;
+    if (!queue->head)
+    {
+        queue->tail = NULL;
+    }
+    thread->wait_queue_next = NULL;
+    return thread;
