+#define TIMEKEEPING_TZ_NAME_MAX 32
+#define TIMEKEEPING_CONFIG_PATH "/etc/timezone/current"
+#define TIMEKEEPING_DAY_SECONDS (24 * 60 * 60)
+
+static uint64_t g_time_base_ms = 0;
+static uint64_t g_tick_base = 0;
+static uint32_t g_tick_frequency = 1000;
+static char g_timezone_name[TIMEKEEPING_TZ_NAME_MAX];
+static int g_timezone_offset_minutes = 0;
+
+static void timekeeping_log(const char *msg)
+{
+    serial_write_string("[time] ");
+    if (msg)
+    {
+        serial_write_string(msg);
+    }
+    serial_write_string("\r\n");
+}
+
+static void timekeeping_set_timezone_internal(const char *name, int offset_minutes)
+{
+    if (!name || *name == '\0')
+    {
+        name = "UTC";
+    }
+    size_t len = strlen(name);
+    if (len >= TIMEKEEPING_TZ_NAME_MAX)
+    {
+        len = TIMEKEEPING_TZ_NAME_MAX - 1;
+    }
+    memcpy(g_timezone_name, name, len);
+    g_timezone_name[len] = '\0';
+    g_timezone_offset_minutes = offset_minutes;
+}
+
+static bool timekeeping_parse_timezone(const char *data,
+                                       size_t size,
+                                       char *name_out,
+                                       size_t name_cap,
+                                       int *offset_out)
+{
+    if (!data || size == 0 || !name_out || name_cap == 0 || !offset_out)
+    {
+        return false;
+    }
+
+    size_t pos = 0;
+    size_t name_len = 0;
+    while (pos < size && data[pos] != '\n' && data[pos] != '\r')
+    {
+        if (name_len + 1 < name_cap)
+        {
+            name_out[name_len++] = data[pos];
+        }
+        pos++;
+    }
+    name_out[name_len] = '\0';
+    while (pos < size && (data[pos] == '\n' || data[pos] == '\r'))
+    {
+        pos++;
+    }
+    if (name_len == 0 || pos >= size)
+    {
+        return false;
+    }
+
+    char offset_buf[32];
+    size_t offset_len = 0;
+    while (pos < size && data[pos] != '\n' && data[pos] != '\r')
+    {
+        if (offset_len + 1 < sizeof(offset_buf))
+        {
+            offset_buf[offset_len++] = data[pos];
+        }
+        pos++;
+    }
+    offset_buf[offset_len] = '\0';
+    int offset = atoi(offset_buf);
+    if (!timekeeping_is_valid_timezone_offset(offset))
+    {
+        return false;
+    }
+    *offset_out = offset;
+    return true;
+}
+
+void timekeeping_init(void)
+{
+    g_tick_frequency = timer_frequency();
+    if (g_tick_frequency == 0)
+    {
+        g_tick_frequency = 1000;
+    }
+    g_tick_base = timer_ticks();
+    g_time_base_ms = 0;
+    timekeeping_set_timezone_internal("UTC", 0);
+    if (!timekeeping_ensure_timezone_config())
+    {
+        timekeeping_log("failed to ensure timezone config");
+    }
+    if (!timekeeping_reload_timezone())
+    {
+        timekeeping_log("using default timezone");
+    }
+}
+
+uint64_t timekeeping_now_millis(void)
+{
+    uint64_t ticks = timer_ticks();
+    uint64_t delta_ticks = ticks - g_tick_base;
+    uint64_t delta_ms = 0;
+    if (g_tick_frequency > 0)
+    {
+        delta_ms = (delta_ticks * 1000ULL) / g_tick_frequency;
+    }
+    return g_time_base_ms + delta_ms;
+}
+
+uint64_t timekeeping_now_seconds(void)
+{
+    return timekeeping_now_millis() / 1000ULL;
+}
+
+bool timekeeping_set_utc_seconds(uint64_t seconds)
+{
+    g_time_base_ms = seconds * 1000ULL;
+    g_tick_base = timer_ticks();
+    return true;
+}
+
+bool timekeeping_save_timezone(const char *name, int offset_minutes)
+{
+    if (!name || *name == '\0')
+    {
+        return false;
+    }
+    if (!timekeeping_is_valid_timezone_offset(offset_minutes))
+    {
+        return false;
+    }
+
+    vfs_node_t *file = vfs_open_file(vfs_root(), TIMEKEEPING_CONFIG_PATH, true, true);
+    if (!file)
+    {
+        return false;
+    }
+
+    char buffer[128];
+    int written = snprintf(buffer, sizeof(buffer), "%s\n%d\n", name, offset_minutes);
+    if (written <= 0)
+    {
+        return false;
+    }
+    if (!vfs_append(file, buffer, (size_t)written))
+    {
+        return false;
+    }
+
+    timekeeping_set_timezone_internal(name, offset_minutes);
+    return true;
+}
+
+bool timekeeping_reload_timezone(void)
+{
+    vfs_node_t *file = vfs_open_file(vfs_root(), TIMEKEEPING_CONFIG_PATH, false, false);
+    if (!file)
+    {
+        return false;
+    }
+    size_t size = 0;
+    const char *data = vfs_data(file, &size);
+    if (!data || size == 0)
+    {
+        return false;
+    }
+    char name[TIMEKEEPING_TZ_NAME_MAX];
+    int offset = 0;
+    if (!timekeeping_parse_timezone(data, size, name, sizeof(name), &offset))
+    {
+        return false;
+    }
+    timekeeping_set_timezone_internal(name, offset);
+    return true;
+}
+
+bool timekeeping_ensure_timezone_config(void)
+{
+    vfs_node_t *file = vfs_open_file(vfs_root(), TIMEKEEPING_CONFIG_PATH, false, false);
