+    char tmp[16];
+    size_t tmp_len = 0;
+    int64_t magnitude = value;
+    bool negative = false;
+    if (magnitude < 0)
+    {
+        negative = true;
+        magnitude = -magnitude;
+    }
+    uint32_t abs_value = (uint32_t)magnitude;
+    do
+    {
+        tmp[tmp_len++] = (char)('0' + (abs_value % 10));
+        abs_value /= 10;
+    } while (abs_value > 0 && tmp_len < sizeof(tmp));
