+    }
+    while (tmp_len > 0)
+    {
+        if (out_len + 1 >= capacity)
+        {
+            return 0;
+        }
+        buffer[out_len++] = tmp[--tmp_len];
+    }
+    buffer[out_len] = '\0';
+    return out_len;
