   446	static void video_blit_clipped(int dst_x0, int dst_y0, int copy_w, int copy_h,
   447	                               const uint8_t *src, int stride_bytes, int src_x0, int src_y0)
   448	{
   449	    const uint8_t *row_ptr = src + (size_t)src_y0 * (size_t)stride_bytes + (size_t)src_x0 * 2U;
   450	    for (int row = 0; row < copy_h; ++row)
   451	    {
   452	        uint16_t *dst = &backbuffer[(dst_y0 + row) * VIDEO_WIDTH + dst_x0];
   453	        memcpy(dst, row_ptr, (size_t)copy_w * 2U);
   454	        row_ptr += stride_bytes;
   455	    }
   456	}
