   458	void video_blit_rgb565(int x, int y, int width, int height, const uint16_t *pixels, int stride_bytes)
   459	{
   460	
   461	    if (!pixels || width <= 0 || height <= 0) return;
   462	    if (stride_bytes <= 0) stride_bytes = width * 2;
   463	
   464	    int x0 = x, y0 = y;
   465	    int x1 = x + width, y1 = y + height;
   466	    int src_x = 0, src_y = 0;
   467	
   468	    if (x0 < 0) { src_x = -x0; x0 = 0; }
   469	    if (y0 < 0) { src_y = -y0; y0 = 0; }
   470	    if (x1 > VIDEO_WIDTH)  x1 = VIDEO_WIDTH;
   471	    if (y1 > VIDEO_HEIGHT) y1 = VIDEO_HEIGHT;
   472	
   473	    int copy_w = x1 - x0;
   474	    int copy_h = y1 - y0;
   475	    if (copy_w <= 0 || copy_h <= 0) return;
   476	
   477	    int max_copy_w = width  - src_x;
   478	    int max_copy_h = height - src_y;
   479	    if (copy_w > max_copy_w) copy_w = max_copy_w;
   480	    if (copy_h > max_copy_h) copy_h = max_copy_h;
   481	    if (copy_w <= 0 || copy_h <= 0) return;
   482	
   483	    video_blit_clipped(x0, y0, copy_w, copy_h, (const uint8_t *)pixels, stride_bytes, src_x, src_y);
   484	    video_invalidate_rect(x0, y0, copy_w, copy_h);
   485	}
