atk_list_node_t *atk_list_push_back(atk_list_t *list, void *value)
{
    if (!list)
    {
        return NULL;
    }

    atk_list_node_t *node = (atk_list_node_t *)malloc(sizeof(atk_list_node_t));
    if (!node)
    {
        return NULL;
    }

    node->value = value;
    node->next = NULL;
    node->prev = list->tail;

    if (list->tail)
    {
        list->tail->next = node;
    }
    else
    {
        list->head = node;
    }

    list->tail = node;
    list->size++;
    return node;
}

void atk_list_remove(atk_list_t *list, atk_list_node_t *node)
{
    if (!list || !node)
    {
        return;
    }

    if (node->prev)
    {
        node->prev->next = node->next;
    }
    else
    {
        list->head = node->next;
    }

    if (node->next)
    {
