atk_widget_t *atk_widget_init(void *memory, const atk_class_t *cls)
{
    if (!memory || !cls)
    {
        return 0;
    }

    size_t size = sizeof(atk_widget_t) + atk_class_total_payload(cls);
    memset(memory, 0, size);

    atk_widget_t *widget = (atk_widget_t *)memory;
    widget->cls = cls;
    widget->used = false;
    widget->parent = 0;
    widget->flags = 0;
