atk_widget_t *atk_widget_create(const atk_class_t *cls)
{
    if (!cls)
    {
        return 0;
    }

    size_t size = sizeof(atk_widget_t) + atk_class_total_payload(cls);
    void *memory = malloc(size);
    if (!memory)
    {
        return 0;
    }

