        return NULL;
    }

    ATK_LIST_FOR_EACH_REVERSE(node, &priv->terminals)
    {
        atk_widget_t *term = (atk_widget_t *)node->value;
        if (!term || !term->used)
        {
            continue;
        }
        int x0 = window->x + term->x;
        int y0 = window->y + term->y;
        int x1 = x0 + term->width;
        int y1 = y0 + term->height;
        if (px >= x0 && px < x1 && py >= y0 && py < y1)
        {
            return term;
        }
    }
    return NULL;
}

