+
+static void atk_build_mouse_event(const atk_widget_t *widget,
+                                  int cursor_x,
+                                  int cursor_y,
+                                  bool pressed_edge,
+                                  bool released_edge,
+                                  bool left_pressed,
+                                  atk_mouse_event_t *event)
+{
