   533	int main(void)
   534	{
   535	    atk_taskmgr_app_t app;
   536	    memset(&app, 0, sizeof(app));
   537	    app.running = true;
   538	
   539	    if (!atk_user_window_open_with_flags(&app.remote,
   540	                                         "Task Manager",
   541	                                         780,
   542	                                         520,
   543	                                         USER_ATK_WINDOW_FLAG_RESIZABLE))
   544	    {
   545	        printf("atk_taskmgr: failed to open remote window\n");
   546	        return 1;
   547	    }
   548	    atk_user_enable_dirty_tracking(&app.remote, true);
   549	
   550	    if (!taskmgr_init_ui(&app))
   551	    {
   552	        printf("atk_taskmgr: failed to init UI\n");
   553	        atk_user_close(&app.remote);
   554	        return 1;
   555	    }
   556	
   557	    memset(g_net_history, 0, sizeof(g_net_history));
   558	    taskmgr_refresh_processes(&app);
   559	    taskmgr_refresh_network(&app);
   560	    taskmgr_render(&app);
   561	
   562	    while (app.running)
   563	    {
   564	        bool needs_render = false;
   565	        user_atk_event_t event;
   566	        while (atk_user_poll_event(&app.remote, &event))
   567	        {
   568	            switch (event.type)
   569	            {
   570	                case USER_ATK_EVENT_MOUSE:
   571	                    taskmgr_handle_mouse(&event, &needs_render);
   572	                    break;
   573	                case USER_ATK_EVENT_KEY:
   574	                    taskmgr_handle_key(&event, &needs_render);
   575	                    break;
   576	                case USER_ATK_EVENT_CLOSE:
   577	                    app.running = false;
   578	                    break;
   579	                case USER_ATK_EVENT_RESIZE:
   580	                    taskmgr_handle_resize(&app, (uint32_t)event.data0, (uint32_t)event.data1);
   581	                    needs_render = true;
   582	                    break;
   583	                default:
   584	                    break;
   585	            }
   586	        }
   587	
   588	        if (!app.running)
   589	        {
   590	            break;
   591	        }
   592	
   593	        app.refresh_counter++;
   594	        if (app.refresh_counter >= TASKMGR_REFRESH_TICKS)
   595	        {
   596	            taskmgr_refresh_processes(&app);
   597	            taskmgr_refresh_network(&app);
   598	            needs_render = true;
   599	            app.refresh_counter = 0;
   600	        }
   601	
   602	        if (needs_render)
   603	        {
   604	            taskmgr_render(&app);
   605	        }
   606	
   607	        sys_yield();
   608	    }
   609	
   610	    atk_user_close(&app.remote);
