    }

    atk_button_priv_t *priv = (atk_button_priv_t *)atk_widget_priv(widget, &ATK_BUTTON_CLASS);
    if (priv)
    {
        priv->list_node = node;
    }

    atk_dirty_mark_rect(widget->x, widget->y, widget->width, atk_button_effective_height(widget));
    return widget;
}

