{
    if (!image)
    {
        return;
    }
    int origin_x = image->parent ? image->parent->x : 0;
    int origin_y = image->parent ? image->parent->y : 0;
    atk_dirty_mark_rect(origin_x + image->x, origin_y + image->y, image->width, image->height);
